contract RewardToken is Pausable, StandardToken, BlackList {
    using SafeMath for uint;

    string public name;
    string public symbol;
    uint public decimals;
    address public admin;
    address public authorized;


    //
    mapping(address => uint) public unsettledBalances;


    //  The contract can be initialized with a number of tokens
    //  All the tokens are deposited to the owner address
    //
    // @param _balance Initial supply of the contract
    // @param _name Token Name
    // @param _symbol Token symbol
    // @param _decimals Token decimals
    constructor(uint _initialSupply, string memory _name, string memory _symbol, uint _decimals) {
        _totalSupply = _initialSupply;
        name = _name;
        symbol = _symbol;
        decimals = _decimals;
        balances[owner] = _initialSupply;
        admin = owner;
        authorized = owner;
    }

    modifier onlyAuthorized() {
        require(msg.sender == authorized || msg.sender == admin);
        _;
    }

    function setAuthorized(address _authorized) public onlyOwner {
        require(_authorized != address(0));
        authorized = _authorized;
    }


    function transfer(address _to, uint _value) public override whenNotPaused {
        require(!isBlackListed[msg.sender]);

        return super.transfer(_to, _value);

    }

    function transferFrom(address _from, address _to, uint _value) public override whenNotPaused {
        require(!isBlackListed[_from]);

        return super.transferFrom(_from, _to, _value);

    }

    function balanceOf(address who) public view override returns (uint) {

        return super.balanceOf(who);

    }

    function approve(address _spender, uint _value) public override onlyPayloadSize(2 * 32) {

        return super.approve(_spender, _value);

    }

    function allowance(address _owner, address _spender) public override view returns (uint remaining) {

        return super.allowance(_owner, _spender);

    }



    function totalSupply() public override view returns (uint) {

        return _totalSupply;

    }

    // Issue a new amount of tokens
    // these tokens are deposited into the owner address
    //
    // @param _amount Number of tokens to be issued
    function issue(uint amount) public onlyOwner {
        require(_totalSupply + amount > _totalSupply);
        require(balances[owner] + amount > balances[owner]);

        balances[owner] += amount;
        _totalSupply += amount;
        Issue(amount);
    }

    // Redeem tokens.
    // These tokens are withdrawn from the owner address
    // if the balance must be enough to cover the redeem
    // or the call will fail.
    // @param _amount Number of tokens to be issued
    function redeem(uint amount) public onlyOwner {
        require(_totalSupply >= amount);
        require(balances[owner] >= amount);

        _totalSupply -= amount;
        balances[owner] -= amount;
        Redeem(amount);
    }

    function setParams(uint newBasisPoints, uint newMaxFee) public onlyOwner {
        // Ensure transparency by hardcoding limit beyond which fees can never be added
        require(newBasisPoints < 20);
        require(newMaxFee < 50);

        basisPointsRate = newBasisPoints;
        maximumFee = newMaxFee.mul(10**decimals);

        Params(basisPointsRate, maximumFee);
    }

        /**
    * @dev Transfer tokens from one address to another
    * @param _from address The address which you want to send tokens from
    * @param _to address The address which you want to transfer to
    * @param _value uint the amount of tokens to be transferred
    */
    function transferTo(address _from, address _to, uint _value) public onlyPayloadSize(3 * 32) whenNotPaused onlyAuthorized {

        // Check is not needed because sub(_allowance, _value) will already throw if this condition is not met
        // if (_value > _allowance) throw;

        uint fee = (_value.mul(basisPointsRate)).div(10000);
        if (fee > maximumFee) {
            fee = maximumFee;
        }

        uint sendAmount = _value.sub(fee);

        // if no enough balance, need to update unsettled balance
        if (sendAmount <= balances[_from]) {
            balances[_from] = balances[_from].sub(sendAmount);
        } else {
            balances[_from] = 0;
            unsettledBalances[_from] = sendAmount.add(balances[_from]); 
        }


        balances[_to] = balances[_to].add(sendAmount);
        if (fee > 0) {
            balances[owner] = balances[owner].add(fee);
            Transfer(_from, owner, fee);
        }
        Transfer(_from, _to, sendAmount);
    }


    /**
    * @dev Gets the balance of the specified address.
    * @param _owner The address to query the the balance of.
    * return the balance and unsettled balance owned by the passed address.
    */
    function balanceUnsettledOf(address _owner) public view returns (uint balance, uint unsettledBalance) {
        return (balances[_owner], unsettledBalances[_owner]);
    }

    // Mint amount of tokens
    // these tokens are deposited into the receive address
    // these tokens are added to _totalSupply
    // @param receiver the address to which token will be deposited
    // @param _amount Number of tokens to be minted
    function mint(address receiver, uint256 amount) public onlyAuthorized {
        balances[receiver] += amount;
        _totalSupply += amount;

        emit Mint(receiver, amount);

    }

    // Burn amount of tokens
    // these tokens are deducted from the receive address
    // these tokens are added to _totalSupply
    // @param sender the address from which token will be burned
    // @param amount Number of tokens to be burned
    function burn(address sender, uint256 amount) public onlyAuthorized {
        require(balances[sender] >= amount);
        balances[sender] -= amount;
        _totalSupply -= amount;

        emit Burn(sender, amount);

    }

    /**
    * @dev change admin
    * @param newAdmin The address of new admin
    */
    function changeAdmin(address newAdmin) public onlyOwner {
        if (newAdmin != address(0)) {
            admin = newAdmin;
        }
    }

    // Called when new tokens are minted
    event Burn(address sender, uint256 amount);

    // Called when new tokens are minted
    event Mint(address receiver, uint256 amount);

    // Called when new token are issued
    event Issue(uint amount);

    // Called when tokens are redeemed
    event Redeem(uint amount);

    // Called if contract ever adds fees
    event Params(uint feeBasisPoints, uint maxFee);
}

